import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Register from './components/Register';
import Profile from './components/Profile';
import { getCurrentUser, logout } from './services/authService';

export default function App(){
  const [route, setRoute] = useState('login');
  const [user, setUser] = useState(null);

  useEffect(()=>{
    const u = getCurrentUser();
    if(u){ setUser(u); setRoute('profile'); }
  },[]);

  function handleLogin(u){ setUser(u); setRoute('profile'); }
  function handleLogout(){ logout(); setUser(null); setRoute('login'); }
  function handleRegister(u){ setUser(u); setRoute('profile'); }
  function goTo(r){ setRoute(r); }

  return (
    <div className="container py-4">
      <nav className="navbar navbar-expand-lg navbar-light bg-light rounded p-3 mb-4">
        <div className="container-fluid">
          <a className="navbar-brand" href="#">AccountApp</a>
          <div className="d-flex gap-2">
            {!user && <button className="btn btn-outline-primary" onClick={()=>goTo('login')}>Login</button>}
            {!user && <button className="btn btn-primary" onClick={()=>goTo('register')}>Register</button>}
            {user && <button className="btn btn-outline-secondary" onClick={handleLogout}>Logout</button>}
          </div>
        </div>
      </nav>

      <main>
        {route === 'login' && <Login onLogin={handleLogin} onGoRegister={()=>goTo('register')} />}
        {route === 'register' && <Register onRegister={handleRegister} onGoLogin={()=>goTo('login')} />}
        {route === 'profile' && <Profile user={user} setUser={setUser} />}
      </main>
    </div>
  );
}
