import React, { useState } from 'react';
import { register, isEmailTaken } from '../services/authService';

export default function Register({ onRegister, onGoLogin }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirm, setConfirm] = useState('');
  const [error, setError] = useState('');

  async function handleSubmit(e) {
    e.preventDefault();
    setError('');
    if (password !== confirm) { setError('Passwords do not match'); return; }
    if (await isEmailTaken(email)) { setError('Email already registered'); return; }
    const user = await register({ name, email, password });
    onRegister(user);
  }

  return (
    <div className="card mx-auto" style={{ maxWidth: '480px' }}>
      <div className="card-body">
        <h5 className="card-title">Register</h5>
        {error && <div className="alert alert-danger">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Full Name</label>
            <input className="form-control" value={name} onChange={e=>setName(e.target.value)} required />
          </div>
          <div className="mb-3">
            <label className="form-label">Email</label>
            <input type="email" className="form-control" value={email} onChange={e=>setEmail(e.target.value)} required />
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input type="password" className="form-control" value={password} onChange={e=>setPassword(e.target.value)} required minLength={6} />
          </div>
          <div className="mb-3">
            <label className="form-label">Confirm Password</label>
            <input type="password" className="form-control" value={confirm} onChange={e=>setConfirm(e.target.value)} required />
          </div>
          <button className="btn btn-primary w-100" type="submit">Create account</button>
        </form>
        <hr />
        <div className="text-center">
          Already registered? <button className="btn btn-link p-0" onClick={onGoLogin}>Login</button>
        </div>
      </div>
    </div>
  );
}
