import React, { useState } from 'react';
import { updateProfile } from '../services/authService';

export default function Profile({ user, setUser }) {
  const [editing, setEditing] = useState(false);
  const [name, setName] = useState(user?.name || '');
  const [email] = useState(user?.email || '');
  const [message, setMessage] = useState('');

  async function save() {
    const updated = await updateProfile({ ...user, name });
    setUser(updated);
    setMessage('Profile updated successfully');
    setEditing(false);
    setTimeout(()=>setMessage(''), 3000);
  }

  return (
    <div className="card mx-auto" style={{ maxWidth: '720px' }}>
      <div className="card-body">
        <h5 className="card-title">Your Profile</h5>
        {message && <div className="alert alert-success">{message}</div>}
        {!editing && (
          <div>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <button className="btn btn-primary me-2" onClick={()=>setEditing(true)}>Edit Profile</button>
          </div>
        )}
        {editing && (
          <div>
            <div className="mb-3">
              <label className="form-label">Name</label>
              <input className="form-control" value={name} onChange={e=>setName(e.target.value)} />
            </div>
            <div className="mb-3">
              <label className="form-label">Email (read-only)</label>
              <input className="form-control" value={email} readOnly />
            </div>
            <button className="btn btn-success me-2" onClick={save}>Save</button>
            <button className="btn btn-secondary" onClick={()=>setEditing(false)}>Cancel</button>
          </div>
        )}
      </div>
    </div>
  );
}
